import styled from "styled-components";

export const CardContainer = styled.div`
    display: flex;
    flex-direction: row;
    
    justify-content:space-between; 
    width: 90%;
    margin: auto;
    background-color:  #35495e;
    padding: 1em; 
    border-radius: 15px;
    box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.6);
`
